import React, { useState } from "react";
import Editor from "@monaco-editor/react";
import axios from "axios";

function App() {
  const [language, setLanguage] = useState("python");
  const [code, setCode] = useState("# Write your code here");
  const [output, setOutput] = useState("");
  const [input, setInput] = useState("");

  const runCode = async () => {
    try {
      const res = await axios.post("http://localhost:5000/run", { code, language, input });
      setOutput(res.data.output || res.data.error);
    } catch (err) {
      setOutput("Error: " + err.message);
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      <div style={{ padding: "10px", background: "#282c34", color: "white" }}>
        <select value={language} onChange={(e) => setLanguage(e.target.value)}>
          <option value="python">Python</option>
          <option value="c">C</option>
          <option value="cpp">C++</option>
          <option value="java">Java</option>
        </select>
        <button onClick={runCode} style={{ marginLeft: "10px" }}>Run</button>
      </div>
      <div style={{ flex: 1 }}>
        <Editor
          height="70%"
          language={language}
          value={code}
          onChange={(value) => setCode(value)}
        />
        <div style={{ display: "flex", height: "30%" }}>
          <textarea
            placeholder="Console input (stdin)"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            style={{ flex: 1, marginRight: "10px", background: "#0f0f0f", color: "#fff", border: "1px solid #333", padding: "10px" }}
          />
          <pre style={{ flex: 1, background: "#1e1e1e", color: "white", padding: "10px", overflow: "auto" }}>
            {output}
          </pre>
        </div>
      </div>
    </div>
  );
}

export default App;
